import React, { useState, useEffect } from "react";
import { Routes, Route } from "react-router-dom";
import Home from "./Home";

import step1 from "./img/step1.png";
import step2 from "./img/step2.png";
import step3 from "./img/step3.png";
// getAPIHealth is defined in our axios-services directory index.js
// you can think of that directory as a collection of api adapters
// where each adapter fetches specific info from our express server's /api route
import { getAPIHealth } from "../axios-services";

const App = () => {
  const [APIHealth, setAPIHealth] = useState("");

  useEffect(() => {
    // follow this pattern inside your useEffect calls:
    // first, create an async function that will wrap your axios service adapter
    // invoke the adapter, await the response, and set the data
    const getAPIStatus = async () => {
      const { healthy } = await getAPIHealth();
      setAPIHealth(healthy ? "api is up! :D" : "api is down :/");
    };

    // second, after you've defined your getter above
    // invoke it immediately after its declaration, inside the useEffect callback
    getAPIStatus();
  }, []);

  return (
    <div className='app-container'>
      <h1>Hello, World!</h1>
      <p>API Status: {APIHealth}</p>
      <>
        <div className='homepage-header-container'>
          <div className='homepage-header'>plantarrium</div>
        </div>
        <div className=''>
          <div className=''>Bring more life to your space</div>
          <div className=''>
            <div className=''>
              <img className='-img' src={step1} alt='' />
              <div className='-copy'>
                <div className='-label'>Step 1</div>
                <div className='-description'>
                  Reach your left arm straight out in front of you and your
                  right leg straight back. Then bend your arm and leg, bringing
                  them into your body until your elbow touches your knee. Repeat
                  with the other arm and leg.
                </div>
              </div>
            </div>

            <div className='steps-to-happiness-card'>
              <img className='steps-to-happiness-img' src={step2} alt='' />
              <div className='steps-to-happiness-copy'>
                <div className='steps-to-happiness-label'>Step 2</div>
                <div className='steps-to-happiness-description'>
                  Stand with your feet hip-width apart. Lift up your left knee
                  to your chest. Switch to lift your right knee to your chest.
                  Continue the movement, alternating legs and moving at a
                  sprinting or running pace.
                </div>
              </div>
            </div>

            <div className='steps-to-happiness-card'>
              <img className='steps-to-happiness-img' src={step3} alt='' />
              <div className='steps-to-happiness-copy'>
                <div className='steps-to-happiness-label'>Step 3</div>
                <div className='steps-to-happiness-description'>
                  Lie on your right side, legs straight and feet stacked. Place
                  your right elbow under your right shoulder with your forearm
                  pointing away from you. Lift your hips off the mat and hold
                  this position then repeat on your left side.
                </div>
              </div>
            </div>
          </div>
        </div>
      </>
    </div>
  );
};

export default App;
